CREATE TABLE Chatbeitrag (ID int NOT NULL AUTO_INCREMENT,NACHRICHT varchar(255) NOT NULL,BENUTZERNAME varchar(20),PRIMARY KEY (ID));
CREATE TABLE Benutzer (ID int NOT NULL AUTO_INCREMENT,BENUTZERNAME varchar(40),PASSWORD varchar(20), EMAIL varchar(40),ROLE varchar(20),INSERT_TIME DATE, PRIMARY KEY (ID));
CREATE TABLE Frage (ID int NOT NULL AUTO_INCREMENT,FRAGENTEXT varchar(255) NOT NULL,ANTWORT_A varchar(255),ANTWORT_B varchar(255),ANTWORT_C varchar(255),ANTWORT_D varchar(255),PRIMARY KEY (ID));